import React, { Component } from "react";
import CatalogItem from "./CatalogItem";
import GifImages from "./GifImages";
import ImageSlider from "./ImageSlider";
import NavBar from "./NavBar";
import '../cssFile/MainPageAfterLogin.css'
import {Animated} from "react-animated-css";



export default class MainPageAfterLogin extends Component {
  constructor(props) {
    super(props);
    this.state={
      ArrCatalog:[{
        imgSrc:"https://www.nvidia.com/content/dam/en-zz/Solutions/geforce/home/Graphics-card-406x320px.jpg",
        catalogName:"Graphic Cards",
        toLink:"/"
      },
      {
        catalogName:"KeyBoards",
        toLink:"/"
      },
      {
        catalogName:"Mouses",
        toLink:"/"
      },
      {
        catalogName:"HeadPhones",
        toLink:"/"
      },
      {
        imgSrc:"https://h1944.co.il/wp-content/uploads/2020/05/cruzer-blade-usb-2-0-angle.png.thumb_.1280.1280.png",
        catalogName:"Usb SanDisk",
        toLink:"/"
      },
      {

        catalogName:"LapTop",
        toLink:"/"
      },
    ]
    }
  }
  render() {
    return (
      <div> 
        <NavBar  Page="MainPageAfterLogin"/>
        <Animated animationIn="fadeInUp" animationOut="bounceOutDown" animationInDuration={2500} animationOutDuration={2500} isVisible={true}>

        <ImageSlider /> 
        <div id="barsCatalog">
       {
         this.state.ArrCatalog.map((e,index)=>{
         return <CatalogItem 
           ToLink={e.ToLink}
           imgSrc={e.imgSrc}
           catalogName={e.catalogName}
           index={index}
           />
         })
       }
         </div>
        <GifImages srcImg="https://d280vd3uzls79w.cloudfront.net/assets/EmailMarketing/All/p19us9n1k01sop1fnd6ln1e981j7mr.gif" />
        </Animated>
      </div>

    );
  }
}
