import React, { Component } from "react";
import CatalogItem from "./CatalogItem";
import GifImages from "./GifImages";
import ImageSlider from "./ImageSlider";
import NavBar from "./NavBar";
import '../cssFile/MainPageAfterLogin.css'
import {Animated} from "react-animated-css";
import AllArrayOfShopsCatalog from "./Arrays/ItemsPagesArr";
import { Route } from "react-router-dom";
import ItemsPage from "./ItemsPage";



export default class MainPageAfterLogin extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div> 
        <NavBar  Page="MainPageAfterLogin"/>
        <Animated animationIn="fadeInUp" animationOut="bounceOutDown" animationInDuration={2500} animationOutDuration={2500} isVisible={true}>

        <ImageSlider /> 
        <div id="barsCatalog">
        {
            AllArrayOfShopsCatalog.map((e,index)=>{
          return(
            <Route exact path={`/ItemsPage-${index}`}>
            <ItemsPage index={index} typeOfCatalog={e.typeOfCatalog} allItems={this.props.allItems}  SetItem={this.props.SetItem} AddToCart={this.props.AddToCart} />
            <CatalogItem 
    ToLink={e.ToLink}
    imgSrc={e.imgSrc}
    catalogName={e.catalogName}
    index={index}
    />
          </Route>
          )   
            })
          }


      
         </div>
        <GifImages srcImg="https://d280vd3uzls79w.cloudfront.net/assets/EmailMarketing/All/p19us9n1k01sop1fnd6ln1e981j7mr.gif" />
        </Animated>
      </div>

    );
  }
}
