import React, { Component } from "react";
import '../cssFile/AdminPage.css'
export default class AdminPage extends Component {
  constructor(props) {
    super(props);
    this.state={
        AdminPageItems:this.props.allItems
    }
  }

  removeItem=(itemID)=>{
console.log(itemID)
this.props.RemoveItem(itemID)

  }

  render() {
    return (
      <div>
        <ul className="list-group">
          {this.state.AdminPageItems.map((item) => {
            return (<li id="Line" className="list-group-item">  <img id="imgLine" src={item.imgsrc} /> <br/> id:{item.id} <br/> {item.title} <br/> Price : {item.price}$
          <br/> 

          <div class="input-group mb-3">
  <input type="text" class="form-control" placeholder="New Price" aria-label="Recipient's username" aria-describedby="basic-addon2"/>
  <div class="input-group-append">
    <button class="btn btn-outline-secondary" type="button">Change Price</button>
  </div></div>

            <br/>  <button className="btn btn-danger" onClick={()=>this.removeItem(item.id)}>remove Item</button>
            </li>
            
            )

          })}
        </ul>
      </div>
    );
  }
}
