import React, { useState, Component } from "react";
import { Route, Switch ,Link} from "react-router-dom";
import LoginPage from "./components/LoginPage";
import MainPageAfterLogin from "./components/MainPageAfterLogin";
import RegisterPage from "./components/RegisterPage";
import { Animated } from "react-animated-css";
import MyProfilePage from "./components/MyProfilePage";
import MyOrders from "./components/MyOrders";
import WishList from "./components/WishList";
import EditProfile from "./components/EditProfile";
import CartPage from "./components/CartPage";
import ItemsPage from "./components/ItemsPage";
import ItemPage from "./components/ItemPage";
import ContactUs from "./components/ContactUs";
import ItemShopArr from './components/Arrays/ItemShopArr'
import AllArrayOfShopsCatalog from './components/Arrays/ItemsPages'
export default function App() {
  const [itemInUse, setItemInUse] = useState(null);
  const [Users , setUsers] = useState([]);
  const [allItems , setAllItems] = useState(ItemShopArr);
  const [cartArray,setCartArray] = useState([]);

  function registerNewUser(e) {
    setItemInUse([...Users,e]);
  }

async function AddToCart(AddToCart,NewList){
  console.log(NewList);
  let temp = cartArray;
  temp.push(AddToCart[0])
 await setCartArray(temp);
 await setAllItems(NewList);
}

  function SetItem(e) {
    setItemInUse(e);
  }

  return (
    <div>
      <Switch>
      
        <Route exact path="/">
          <LoginPage />
        </Route>

        <Route exact path="/Register">
          <Animated
            animationIn="zoomInDown"
            animationOut="flipOutX"
            animationInDuration={1000}
            animationOutDuration={1000}
            isVisible={true}
          >
            <RegisterPage />
          </Animated>
        </Route>

        <Route exact path="/MainPage">
          <Animated
            animationIn="fadeIn"
            animationOut="rotateOutDownRight"
            animationInDuration={2500}
            animationOutDuration={2500}
            isVisible={true}
          >
            <MainPageAfterLogin />
            <Link to="/ItemsPage-0">letsgo</Link>
          </Animated>
        </Route>

        <Route exact path="/ProfilePage">
          <MyProfilePage
            imgSrc="https://i.pinimg.com/564x/04/bb/21/04bb2164bbfa3684118a442c17d086bf.jpg"
            name="mosaab abo rkia"
          />
        </Route>

        <Route exact path="/MyOrders">
          <MyOrders />
        </Route>

        <Route exact path="/WishList">
          <WishList />
        </Route>

        <Route exact path="/EditProfile">
          <EditProfile />
        </Route>

        <Route exact path="/CartPage">
          <CartPage  cartArray={cartArray}/>
        </Route>

        
          {
            AllArrayOfShopsCatalog.map((e,index)=>{
              console.log(index);
          return(
            <Route exact path={`/ItemsPage-${index}`}>
            <ItemsPage typeOfCatalog={e.typeOfCatalog} allItems={allItems}  SetItem={SetItem} AddToCart={AddToCart} />
          </Route>
          )   
            })
          }
          

        <Route exact path="/ItemPage">
          <ItemPage itemInUse={itemInUse}  AddToCart={AddToCart}/>
        </Route>

        <Route exact path="/ContactUs">
          <ContactUs />
        </Route>
      </Switch>
    </div>
  );
}
