import React, { Component } from 'react'
import '../cssFile/LoginPage.css'
import {Animated} from "react-animated-css";
import { Link, withRouter } from 'react-router-dom';
function LoginPage(props) {

    
    function CheckLogin(e){
        e.preventDefault();
        console.log(this.props.Users)
        console.log("joined")
        this.props.Users.map(e=>{
            if(e.email === this.state.loginEmail && e.password===this.state.loginPassword){
if(this.state.saveLogin === true){
localStorage.setItem('saveLogin',true);
}
localStorage.setItem("User",e);
console.log(this.state.saveLogin)
this.props.history.push("/MainPage");
            }
        })
        this.setState({message:'Ops Something Wrong Try Again'})
    }
    

  function onChangeHandleEmail(e){

    if (!this.validateEmail(e.target.value)) {
     return;
    }
    
    this.setState({ [e.target.name]: e.target.value });
  };
  function onChangeHandleSaveLogin(e){
   this.setState({saveLogin:e.target.checked})
  }

        return (
            <div>
             <Animated animationIn="fadeIn" animationInDuration={1400} animationOutDuration={1400} isVisible={true}>
                <div>
               <img id="imgLogin" alt="ImgLogin" src="https://airspeed.ph/wp-content/uploads/2020/06/The-Importance-of-Courier-Services-for-Online-Shopping-of-Essential-Items.jpg" /> 
               <img id="LOGOlog" alt="LOGOlog" src="https://i.ibb.co/CbBF9Hn/LOGO.png"/>
                </div>
                <form onSubmit={this.CheckLogin}>
                <div id="DivLoginInputs">
                <p id="typeForInput">Email Adress</p>
                <input name="loginEmail" onChange={this.onChangeHandle} className="loginInput" id="loginInputEmail" type="email" placeholder="Enter Email Adress" required/>
               <p id="typeForInput" >Password</p>
               <input name="loginPassword" onChange={this.onChangeHandle} className="loginInput" id="loginInputPassword" type="password" placeholder="Enter Password" required/>
                </div>

               <div id="savePasswordInput"><input name="saveLogin" onChange={this.onChangeHandleSaveLogin}  type="checkbox" name="savePassword" />  save your password </div>
               {this.state.message}
             <div id="buttonLoginDiv"><input id="ButtonLogin" type="submit" value="Login" /></div>
             </form>
               <div id="goToRegisterFromLogin">
               <Link to="/register">you dont have account already? Register and big Discount !!</Link>
               </div>
               </Animated>
            </div>
        )
    }

export default withRouter(LoginPage)
