import React,{useState} from "react";
import EachLine from "./EachLine";
import NavBar from "./NavBar";



export default function CartPage() {
  let [ArrMyOrder,setArrMyOrder] = useState([
    {
      srcImg:
        "https://images.unsplash.com/photo-1490077476659-095159692ab5?ixid=MXwxMjA3fDB8MHxzZWFyY2h8MXx8c2NlbmV8ZW58MHx8MHw%3D&ixlib=rb-1.2.1&w=1000&q=80",
      orderId: "12352324",
      totalPrice: 100,
    },
    {
      srcImg:
        "https://images.unsplash.com/photo-1490077476659-095159692ab5?ixid=MXwxMjA3fDB8MHxzZWFyY2h8MXx8c2NlbmV8ZW58MHx8MHw%3D&ixlib=rb-1.2.1&w=1000&q=80",
      orderId: "12356324",
      totalPrice: 75,
    },
    {
      srcImg:
        "https://images.unsplash.com/photo-1490077476659-095159692ab5?ixid=MXwxMjA3fDB8MHxzZWFyY2h8MXx8c2NlbmV8ZW58MHx8MHw%3D&ixlib=rb-1.2.1&w=1000&q=80",
      orderId: "12359324",
      totalPrice: 25,
    },
  ]);




  let total = 0;
  function RemoveOne(OrderId){

    let temp = /* props.*/ArrMyOrder.filter(e=>{return e.orderId !== OrderId })

    setArrMyOrder(temp) 
    
  }

  

  return (
    <div>
      <NavBar Page="MainPageAfterLogin" toLink="/MainPage" />
      <ul key="List" className="list-group">
        {
          /* props.*/ ArrMyOrder.map((e) => {
            total += e.totalPrice;
            return <EachLine e={e} RemoveOne={RemoveOne} /> ;
          })
        }
      <h1>  Total : {total}$</h1>
      </ul>
      <div style={{textAlign:"center", display:total!==0?"block":"none"}}> <button className="btn btn-primary">Pay All</button></div>
     
    </div>
  );
}
