import React, { Component } from "react";
import "../cssFile/RegisterPage.css";
export default class RegisterPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
 
    };
  }

  validateEmail = (email) => {
    const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  };

  onChangeHandle=(e)=>{
      if([e.target.name]==='email'){
          if(!this.validateEmail(e.target.value))
          return;
      }
    this.setState({[e.target.name]:e.target.value})
  }

  render() {
    return (
      <div className="container RegisterPage">
        <div id="titlesRegisterP">
          <a href="/">
            <img
              style={{ position: "absolute" }}
              alt="backArrow"
              id="backArrow"
              src="https://cdn2.iconfinder.com/data/icons/arrows-and-universal-actions-icon-set/256/arrow_left_circle-256.png"
            />
          </a>
          <img
            id="LOGOReg"
            alt="LOGOReg"
            src="https://i.ibb.co/CbBF9Hn/LOGO.png"
          />
        </div>
        <div id="RegisterForm">
          <h4 id="titleOfRegister">Sign Up</h4>
          <p id="typeForInput">First Name</p>
          <input
          name="firstName"
          onChange={this.onChangeHandle}
            className="makeSpace"
            id="firstNameReg"
            type="text"
            placeholder="Enter First Name"
          />
          <p id="typeForInput">Last Name</p>
          <input
                    name="lastNameReg"
                    onChange={this.onChangeHandle}
            className="makeSpace"
            id="lastNameReg"
            type="text"
            placeholder="Enter Last Name"
          />
          <p id="typeForInput">Email Adress</p>
          <input
            className="makeSpace"
            id="emailReg"
            type="email"
            placeholder="Enter Email"
          />
          <p id="typeForInput">Password</p>
          <input
                              name="password"
                              onChange={this.onChangeHandle}
            className="makeSpace"
            id="passtReg"
            type="password"
            placeholder="Enter Password"
          />
          <p id="typeForInput">Confirm Password</p>
          <input
                                        name="conPassword"
                                        onChange={this.onChangeHandle}
            className="makeSpace"
            id="conPassReg"
            type="password"
            placeholder="Enter Password Again"
          />
          <small>We Never Save Your Password !!!</small>
          <input className="makeSpaceButton" type="button" value="Sign Up" />
        </div>
      </div>
    );
  }
}
