import React,{Component, useState} from "react";
import EachLine from "./EachLine";
import NavBar from "./NavBar";

export default function CartPage(props) {
      const [cartArr,setCartArr] = useState(props.cartArray)
      let [total,setTotal] = useState(0); 
    
    function RemoveOne(e1){
    let toRemove = this.state.cartArr.filter(e=>{ return e.id === parseInt(e1)})
    let newArrCart = this.state.cartArr.filter(e=>{ return e.id !== parseInt(e1)})
   props.removeFromCart(toRemove,newArrCart);
   setCartArr(newArrCart)
  }

   function ChangeQuantity(id,quantity){
    console.log(quantity);
    this.props.ChangeQuantity(id,quantity);
  }



  return (
    <div>
      <NavBar Page="CartPage" toLink="/MainPage" />
      <ul key="List" className="list-group">
        {
           this.state.cartArr.map((e) => {
            this.state.total += parseInt(e.price*e.quantity);
            return <EachLine ChangeQuantity={ChangeQuantity} e={e} RemoveOne={RemoveOne} /> ;
          })
        }
      <h1>  Total : {this.state.total}$</h1>
      </ul>
      <div style={{textAlign:"center", display:this.total!==0?"block":"none"}}> <button className="btn btn-primary">Pay All</button></div>
     
    </div>
  );
}
