import React,{useState} from "react";
import EachLine from "./EachLine";
import NavBar from "./NavBar";



export default function CartPage(props) {
  let total = 0;
  const [cartArr,setCartArr] = useState(props.cartArray);

  async function RemoveOne(e1){
    let toRemove = cartArr.filter(e=>{ return e.id === parseInt(e1)})
    console.log(toRemove);
    let newArrCart = cartArr.filter(e=>{ return e.id === parseInt(e1)})
    console.log(newArrCart);

    await props.removeFromCart(toRemove);
    await setCartArr(newArrCart);
    /*console.log(e1)
    console.log('here array')
    console.log(cartArr)
    let toRemove = cartArr.filter(e=>{return e.id === e1})  
    console.log('here remove')
    console.log(toRemove)
    let newArrCart = cartArr.filter(e=>{return e.id !== e1}) 
    console.log(newArrCart)
    props.removeFromCart(toRemove);
    setCartArr(newArrCart);*/
  }
  

  return (
    <div>
      <NavBar Page="CartPage" toLink="/MainPage" />
      <ul key="List" className="list-group">
        {
           cartArr.map((e) => {
            total += parseInt(e.price);
            return <EachLine e={e} RemoveOne={RemoveOne} /> ;
          })
        }
      <h1>  Total : {total}$</h1>
      </ul>
      <div style={{textAlign:"center", display:total!==0?"block":"none"}}> <button className="btn btn-primary">Pay All</button></div>
     
    </div>
  );
}
