import React, { useState, useEffect } from "react";
import EachListItem from "./EachListItem";
import NavBar from "./NavBar";

export default function ItemsPage(props) {
  const [catalogList, setCatalogList] = useState([]);

function CheckifDeleted(e){
  props.cartArray.forEach(e1=>{
if(e1.id === e.id){
  return false;
}
else{
 return true;
}
  })
}

  useEffect(() => {
    let temp = props.allItems.filter((item) => {
      return item.type === props.typeOfCatalog && CheckifDeleted(item);
    });
console.log(temp)
    setCatalogList(temp);
  }, []);

  function setItemToMain(e) {
    props.SetItem(e);
  }

  function AddToCart(eId) {
    let ToAddInCart = catalogList.filter((e) => {
      return e.id === eId;
    });
    let NewList = catalogList.filter((e) => {
      return e.id !== eId;
    });
    setCatalogList(NewList);
    console.log(ToAddInCart)
    props.AddToCart(ToAddInCart,props.index);
    
  }

  return (
    <div>
      <NavBar Page="MainPageAfterLogin" toLink="/MainPage" />

      <ul>
        {catalogList.map((e) => {
          return (
            <EachListItem
              setItemToMain={setItemToMain}
              AddToCart={AddToCart}
              Item={e}
              />
          );
        })}
      </ul>
    </div>
  );
}
