import EachLine from "./EachLine";
import NavBar from "./NavBar";



export default function CartPage() {
  const [text,setText] = useState(0);
  const [ArrMyOrder,setArrMyOrder] = useState([
    {
      srcImg:
        "https://images.unsplash.com/photo-1490077476659-095159692ab5?ixid=MXwxMjA3fDB8MHxzZWFyY2h8MXx8c2NlbmV8ZW58MHx8MHw%3D&ixlib=rb-1.2.1&w=1000&q=80",
      orderId: "12352324",
      totalPrice: 100,
    },
    {
      srcImg:
        "https://images.unsplash.com/photo-1490077476659-095159692ab5?ixid=MXwxMjA3fDB8MHxzZWFyY2h8MXx8c2NlbmV8ZW58MHx8MHw%3D&ixlib=rb-1.2.1&w=1000&q=80",
      orderId: "12356324",
      totalPrice: 75,
    },
    {
      srcImg:
        "https://images.unsplash.com/photo-1490077476659-095159692ab5?ixid=MXwxMjA3fDB8MHxzZWFyY2h8MXx8c2NlbmV8ZW58MHx8MHw%3D&ixlib=rb-1.2.1&w=1000&q=80",
      orderId: "12359324",
      totalPrice: 25,
    },
  ]);

useEffect(() => {
  TextSet();
}, [])

function TextSet(){
  if(ArrMyOrder.length!==0){
    setText(`Total : ${total}`)
  }
  else{
    setText(`<h1>Empty Cart</h1>`)
  }
}


