import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "../cssFile/AdminPage.css";

class AdminPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      AdminPageItems: this.props.allItems,
      newPrice: 0,
      ID: null,
      type: null,
      imgsrc: null,
      title: null,
      price: null,
      description: null,
    };
  }

  removeItem = (itemID) => {
    console.log(itemID);
    this.props.RemoveItem(itemID);
    let temp = [];
    this.state.AdminPageItems.map((item) => {
      if (item.id !== itemID) {
        temp.push(item);
      }
    });
    this.setState({ AdminPageItems: temp });
  };
  ChangePrice = (itemID) => {
    console.log(itemID);
    let temp = [];
    this.state.AdminPageItems.map((item) => {
      if (item.id === itemID) {
        item.price = this.state.newPrice;
      }
      temp.push(item);
    });
    this.setState({ AdminPageItems: temp });
  };

  HandlePrice = (e) => {
    this.setState({ newPrice: e.target.value });
  };
  HandleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  CreateNewItem = () => {
    let NewItem = {
      id: this.state.id,
      type: this.state.type,
      id: this.state.id,
      type: this.state.type,
      id: this.state.id,
      type: this.state.type,
    };
  };

  render() {
    return (
      <div>
        <div id="AddItems">
          <h1 style={{ color: "green" }}>Add Items</h1>
          <input
            type="text"
            name="ID"
            onChange={this.HandleChange}
            placeholder="ID"
          />
          <input
            type="text"
            name="type"
            onChange={this.HandleChange}
            placeholder="type"
          />
          <input
            type="text"
            name="imgsrc"
            onChange={this.HandleChange}
            placeholder="imgsrc"
          />
          <input
            type="text"
            name="title"
            onChange={this.HandleChange}
            placeholder="title"
          />
          <input
            type="text"
            name="price"
            onChange={this.HandleChange}
            placeholder="price"
          />
          <input
            type="text"
            name="description"
            onChange={this.HandleChange}
            placeholder="description"
          />
          <input type="button" onClick={this.CreateNewItem} value="Add Item" />
        </div>
        <ul className="list-group">
          {this.state.AdminPageItems.map((item) => {
            return (
              <li id="Line" className="list-group-item">
                <img id="imgLine" src={item.imgsrc} />
                <br /> id:{item.id} <br /> {item.title}
                <br /> Price : {item.price}$
                <br />
                <div class="input-group mb-3">
                  <input
                    type="text"
                    onChange={this.HandlePrice}
                    class="form-control"
                    placeholder="New Price"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                  />
                  <div class="input-group-append">
                    <button
                      class="btn btn-outline-secondary"
                      onClick={() => this.ChangePrice(item.id)}
                      type="button"
                    >
                      Change Price
                    </button>
                  </div>
                </div>
                <br />{" "}
                <button
                  className="btn btn-danger"
                  onClick={() => this.removeItem(item.id)}
                >
                  remove Item
                </button>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}
//لانو اذا محطناش اروه فنكشن بشغلهن لما يبنيهن بدون ما تعص
export default withRouter(AdminPage);
