import React,{Component, useState} from "react";
import EachLine from "./EachLine";
import NavBar from "./NavBar";

export default class CartPage extends Component {
  constructor(props){
    super(props);
    this.state={
      cartArr:props.cartArray,
      total:0
    }
  }
