import React, { Component } from 'react'
import '../cssFile/LoginPage.css'
import {Animated} from "react-animated-css";
import { Link, withRouter } from 'react-router-dom';
class LoginPage extends Component {
    constructor(props) {
        super(props);
        this.state={
            message:"",
            loginEmail:null,
            loginPassword:null,
            saveLogin:null
        }
    }
    CheckLogin=()=>{
        this.props.Users.map(e=>{
            if(e.email === this.state.loginEmail && e.password===this.state.loginPassword){
if(this.state.saveLogin){
localStorage.setItem('saveLogin',true);
}
localStorage.setItem("User",e);
this.props.history.push("/MainPage")
            }
        })
    }
    

  onChangeHandle = (e) => {
    if ([e.target.name] === "email") {
      if (!this.validateEmail(e.target.value)) return;
    }
    this.setState({ [e.target.name]: e.target.value });
  };

    render() {
        return (
            <div>
             <Animated animationIn="fadeIn" animationInDuration={1400} animationOutDuration={1400} isVisible={true}>
                <div>
               <img id="imgLogin" alt="ImgLogin" src="https://airspeed.ph/wp-content/uploads/2020/06/The-Importance-of-Courier-Services-for-Online-Shopping-of-Essential-Items.jpg" /> 
               <img id="LOGOlog" alt="LOGOlog" src="https://i.ibb.co/CbBF9Hn/LOGO.png"/>
                </div>
                <form onSubmit={this.CheckLogin}>
                <div id="DivLoginInputs">
                <p id="typeForInput">Email Adress</p>
                <input name="loginEmail" onChange={this.onChangeHandle} className="loginInput" id="loginInputEmail" type="text" placeholder="Enter Email Adress"/>
               <p id="typeForInput" >Password</p>
               <input name="loginPassword" onChange={this.onChangeHandle} className="loginInput" id="loginInputPassword" type="password" placeholder="Enter Password"/>
                </div>

               <div id="savePasswordInput"><input name="saveLogin" onChange={this.onChangeHandle}  type="checkbox" name="savePassword" />  save your password </div>
             <div id="buttonLoginDiv"><input id="ButtonLogin" type="button" value="Login" /></div>
             </form>
               <div id="goToRegisterFromLogin">
               <Link to="/register">you dont have account already? Register and big Discount !!</Link>
               </div>
               </Animated>
            </div>
        )
    }
}
export default withRouter(LoginPage)
